    /// Represents a single search result item from transcript analysis    /// </summary>    public class SearchResultItem    {            /// Gets or sets the text content of the search result        /// </summary>        public string Text { get; set; } = string.Empty;                    /// Gets or sets the start time in milliseconds        /// </summary>        public int StartTimeMs { get; set; }                    /// Gets or sets the end time in milliseconds        /// </summary>        public int EndTimeMs { get; set; }                    /// Gets or sets the confidence score        /// </summary>        public double Confidence { get; set; }                    /// Gets or sets the source transcription ID        /// </summary>        public string TranscriptionId { get; set; } = string.Empty;                    /// Gets or sets the type of search result (Word, Sentiment, Highlight, Chapter)        /// </summary>        public string Type { get; set; } = string.Empty;                    /// Gets or sets the sentiment (positive, neutral, negative) if applicable        /// </summary>        public string Sentiment { get; set; } = string.Empty;                    /// Gets the formatted time string in mm:ss format        /// </summary>        public string FormattedTime => TimeSpan.FromMilliseconds(StartTimeMs).ToString(@"mm\:ss");                    /// Gets the brush color for sentiment display        /// </summary>        public Brush SentimentColor        {            get            {                return Sentiment?.ToLower() switch                {                    "positive" => Brushes.LightGreen,                    "negative" => Brushes.IndianRed,                    "neutral" => Brushes.LightGray,                    _ => Brushes.White                };            }        }    }}
